import React from 'react';
import './MainVideo.scss';

const MainVideo = ({ src, poster }) => (
  <div className="videoPlayer">
    <video src={src} controls poster={poster ? poster : 'path/to/default/placeholder.jpg'} />
  </div>
);

export default MainVideo;
