import "./UserAvater.scss";

// Styles for the avatar
const avatarStyle = {
    width: '50px',  // Adjust size as needed
    height: '50px', // Adjust size as needed
    borderRadius: '50%', // Circular shape
    objectFit: 'cover', // Ensures the image covers the area nicely
};

const UserAvatar = ({ imageUrl, altText }) => {
    // URL for a default avatar image if none is provided
    const defaultAvatar = "../../assets/images/Mohan-muruge.jpg"; 

    return (
        <img 
            src={imageUrl || defaultAvatar} 
            alt={altText || 'User Avatar'} 
            style={avatarStyle} 
        />
    );
};

export default UserAvatar;
